async function execute(input, credentials) {
  const { action, postId, content, visibility = 'PUBLIC', companyId, profileId, authorType = 'person' } = input;
  const { ACCESS_TOKEN } = credentials;

  const baseUrl = 'https://api.linkedin.com/v2';
  const headers = {
    'Authorization': `Bearer ${ACCESS_TOKEN}`,
    'Content-Type': 'application/json',
    'X-Restli-Protocol-Version': '2.0.0'
  };

  try {
    if (action === 'publish') {
      const author = companyId ? `urn:li:organization:${companyId}` : `urn:li:person:${profileId}`;

      const postData = {
        author,
        lifecycleState: 'PUBLISHED',
        specificContent: {
          'com.linkedin.ugc.ShareContent': {
            shareCommentary: {
              text: content
            },
            shareMediaCategory: 'NONE'
          }
        },
        visibility: {
          'com.linkedin.ugc.MemberNetworkVisibility': visibility
        }
      };

      const response = await fetch(`${baseUrl}/ugcPosts`, {
        method: 'POST',
        headers,
        body: JSON.stringify(postData)
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || 'Failed to publish post' };
      }

      const result = await response.json();
      return { success: true, data: { id: result.id, status: 'published' } };
    }

    if (action === 'read') {
      if (!postId) {
        return { success: false, error: 'postId is required for read action' };
      }

      const response = await fetch(`${baseUrl}/ugcPosts/${encodeURIComponent(postId)}`, {
        method: 'GET',
        headers
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || 'Failed to read post' };
      }

      const result = await response.json();
      return { success: true, data: result };
    }

    if (action === 'list') {
      const author = companyId ? `urn:li:organization:${companyId}` : `urn:li:person:${profileId}`;
      const params = new URLSearchParams({
        q: 'authors',
        authors: author,
        count: '50'
      });

      const response = await fetch(`${baseUrl}/ugcPosts?${params}`, {
        method: 'GET',
        headers
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || 'Failed to list posts' };
      }

      const result = await response.json();
      return { success: true, data: result.elements || [] };
    }

    if (action === 'update') {
      if (!postId) {
        return { success: false, error: 'postId is required for update action' };
      }

      const updateData = {
        patch: {
          $set: {
            specificContent: {
              'com.linkedin.ugc.ShareContent': {
                shareCommentary: {
                  text: content
                }
              }
            }
          }
        }
      };

      const response = await fetch(`${baseUrl}/ugcPosts/${encodeURIComponent(postId)}`, {
        method: 'POST',
        headers: {
          ...headers,
          'Content-Type': 'application/json-patch+json'
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || 'Failed to update post' };
      }

      return { success: true, data: { id: postId, status: 'updated' } };
    }

    if (action === 'delete') {
      if (!postId) {
        return { success: false, error: 'postId is required for delete action' };
      }

      const response = await fetch(`${baseUrl}/ugcPosts/${encodeURIComponent(postId)}`, {
        method: 'DELETE',
        headers
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || 'Failed to delete post' };
      }

      return { success: true, data: { id: postId, status: 'deleted' } };
    }

    return { success: false, error: `Unsupported action: ${action}` };

  } catch (error) {
    return { success: false, error: error.message };
  }
}
