async function execute(input, credentials) {
  const { action, title, content, format } = input;

  if (action === 'publish' || action === 'save') {
    const slug = (title || 'untitled').toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '');
    const date = new Date().toISOString().split('T')[0];
    const filename = `${date}-${slug}`;

    // Return the content to be saved (executor will handle file I/O)
    return {
      success: true,
      data: {
        filename,
        title,
        content,
        format: format || 'markdown',
        savedAt: new Date().toISOString()
      }
    };
  }

  return { success: false, error: `Unknown action: ${action}` };
}
