async function execute(input, credentials) {
  const { action, postId, title, content, status = 'publish', postType = 'posts', slug, excerpt, categories, tags, featuredMedia, page = 1, perPage = 10, search, author } = input;
  const { API_KEY, BASE_URL, USERNAME } = credentials;

  const baseUrl = BASE_URL.endsWith('/') ? BASE_URL.slice(0, -1) : BASE_URL;
  const apiUrl = `${baseUrl}/wp-json/wp/v2`;

  const headers = {
    'Content-Type': 'application/json',
    'Authorization': `Basic ${btoa(`${USERNAME}:${API_KEY}`)}`
  };

  try {
    if (action === 'create' || action === 'publish') {
      if (!title || !content) {
        return { success: false, error: 'Title and content are required for creating posts' };
      }

      const postData = {
        title,
        content,
        status,
        excerpt: excerpt || '',
        slug: slug || title.toLowerCase().replace(/[^a-z0-9]+/g, '-')
      };

      if (categories) postData.categories = categories;
      if (tags) postData.tags = tags;
      if (featuredMedia) postData.featured_media = featuredMedia;

      const response = await fetch(`${apiUrl}/${postType}`, {
        method: 'POST',
        headers,
        body: JSON.stringify(postData)
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || `HTTP ${response.status}` };
      }

      const result = await response.json();
      return { success: true, data: result };
    }

    if (action === 'read' || action === 'get') {
      if (!postId) {
        return { success: false, error: 'Post ID is required for reading posts' };
      }

      const response = await fetch(`${apiUrl}/${postType}/${postId}`, {
        headers: { 'Authorization': headers.Authorization }
      });

      if (!response.ok) {
        if (response.status === 404) {
          return { success: false, error: 'Post not found' };
        }
        return { success: false, error: `HTTP ${response.status}` };
      }

      const result = await response.json();
      return { success: true, data: result };
    }

    if (action === 'list') {
      const params = new URLSearchParams({
        page: page.toString(),
        per_page: perPage.toString()
      });

      if (search) params.append('search', search);
      if (author) params.append('author', author.toString());
      if (status && status !== 'any') params.append('status', status);

      const response = await fetch(`${apiUrl}/${postType}?${params}`, {
        headers: { 'Authorization': headers.Authorization }
      });

      if (!response.ok) {
        return { success: false, error: `HTTP ${response.status}` };
      }

      const result = await response.json();
      const totalPosts = response.headers.get('X-WP-Total');
      const totalPages = response.headers.get('X-WP-TotalPages');

      return {
        success: true,
        data: {
          posts: result,
          pagination: {
            page,
            perPage,
            total: totalPosts ? parseInt(totalPosts) : result.length,
            totalPages: totalPages ? parseInt(totalPages) : 1
          }
        }
      };
    }

    if (action === 'update') {
      if (!postId) {
        return { success: false, error: 'Post ID is required for updating posts' };
      }

      const updateData = {};
      if (title) updateData.title = title;
      if (content) updateData.content = content;
      if (status) updateData.status = status;
      if (excerpt) updateData.excerpt = excerpt;
      if (slug) updateData.slug = slug;
      if (categories) updateData.categories = categories;
      if (tags) updateData.tags = tags;
      if (featuredMedia) updateData.featured_media = featuredMedia;

      if (Object.keys(updateData).length === 0) {
        return { success: false, error: 'At least one field must be provided for update' };
      }

      const response = await fetch(`${apiUrl}/${postType}/${postId}`, {
        method: 'POST',
        headers,
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        const error = await response.json();
        return { success: false, error: error.message || `HTTP ${response.status}` };
      }

      const result = await response.json();
      return { success: true, data: result };
    }

    if (action === 'delete') {
      if (!postId) {
        return { success: false, error: 'Post ID is required for deleting posts' };
      }

      const response = await fetch(`${apiUrl}/${postType}/${postId}`, {
        method: 'DELETE',
        headers
      });

      if (!response.ok) {
        if (response.status === 404) {
          return { success: false, error: 'Post not found' };
        }
        return { success: false, error: `HTTP ${response.status}` };
      }

      const result = await response.json();
      return { success: true, data: result };
    }

    return { success: false, error: `Unsupported action: ${action}` };

  } catch (error) {
    return { success: false, error: error.message };
  }
}
